export function getMoviesByCategory(category: string): Movie[] {
  // In a real app, this would filter based on actual categories
  switch (category) {
    case "popular":
      return allMovies.slice(0, 12)
    case "new":
      return allMovies.slice(3, 15)
    case "action":
      return allMovies.filter((movie) => movie.genres.includes("Action"))
    case "comedy":
      return allMovies.filter((movie) => movie.genres.includes("Comedy"))
    case "drama":
      return allMovies.filter((movie) => movie.genres.includes("Drama"))
    case "animation":
      return allMovies.filter((movie) => movie.genres.includes("Animation"))
    case "rwandan":
      return rwandanMovies
    case "all":
      return allMovies
    default:
      return allMovies
  }
}

export function getRwandanMovies(): Movie[] {
  return rwandanMovies
}

export function getCategoryTitle(category: string): string {
  switch (category) {
    case "popular":
      return "Popular Movies"
    case "new":
      return "New Releases"
    case "action":
      return "Action Movies"
    case "comedy":
      return "Comedy Movies"
    case "drama":
      return "Drama Movies"
    case "animation":
      return "Animation Movies"
    case "rwandan":
      return "Rwandan Movies"
    case "all":
      return "All Movies"
    default:
      return "Movies"
  }
}

export function sortMovies(movies: Movie[], sortBy: string): Movie[] {
  const sortedMovies = [...movies]

  switch (sortBy) {
    case "newest":
      return sortedMovies.sort((a, b) => b.year - a.year)
    case "oldest":
      return sortedMovies.sort((a, b) => a.year - b.year)
    case "a-z":
      return sortedMovies.sort((a, b) => a.title.localeCompare(b.title))
    case "rating":
      // This is a placeholder - in a real app, you'd have actual ratings
      return sortedMovies.sort((a, b) => a.id.localeCompare(b.id))
    default:
      return sortedMovies
  }
}

export function getMovieById(id: string): Movie | undefined {
  return allMovies.find((movie) => movie.id === id)
}

export function getRelatedMovies(id: string): Movie[] {
  const movie = getMovieById(id)
  if (!movie) return allMovies.slice(0, 6)

  // Find movies with similar genres
  return allMovies.filter((m) => m.id !== id && m.genres.some((genre) => movie.genres.includes(genre))).slice(0, 6)
}

export function searchMovies(query: string): Movie[] {
  if (!query) return []

  const lowercaseQuery = query.toLowerCase()
  return allMovies.filter(
    (movie) =>
      movie.title.toLowerCase().includes(lowercaseQuery) ||
      movie.description.toLowerCase().includes(lowercaseQuery) ||
      movie.genres.some((genre) => genre.toLowerCase().includes(lowercaseQuery)) ||
      movie.director.toLowerCase().includes(lowercaseQuery) ||
      movie.cast.some((actor) => actor.toLowerCase().includes(lowercaseQuery)),
  )
}

export function getMoviesByRegion(region: string): Movie[] {
  if (region.toLowerCase() === "all") return allMovies

  const formattedRegion = region.charAt(0).toUpperCase() + region.slice(1).toLowerCase()
  return allMovies.filter((movie) => movie.region === formattedRegion)
}

export function getMoviesByTranslator(translator: string): Movie[] {
  if (translator.toLowerCase() === "all") return allMovies

  return allMovies.filter((movie) => movie.translator === translator)
}

export function getMoviesByGenre(genre: string): Movie[] {
  if (genre.toLowerCase() === "all") return allMovies

  return allMovies.filter((movie) => movie.genres.some((g) => g.toLowerCase() === genre.toLowerCase()))
}

